UPDATE dmsodutk as cd RIGHT JOIN tenmuc as dm ON cd.tkhoan = dm.tkhoan SET cd.tentk = dm.tenkhoan WHERE cd.nam=cnam AND TRIM(cd.tkhoan) <> '' ;
END$$

